mapper.update:
UPDATE PEN5000 SET
MODIFY_DTM		= CURRENT_TIMESTAMP
,MODIFY_USR_ID = #{usrId}
,MODIFY_USR_IP = #{accessIp}
,EFORM_YN= #{eformYn}
,DEL_GBN_CD = #{delGbnCd}
,ORD_WAY_NUM = #{ordWayNum}
,DEL_SER_CD = #{delSerCd}
,ORD_NM = #{ordNm}
,ORD_CONT = #{ordCont}
,ORD_MEMO = #{ordMemo}
,ORD_ZIP = #{ordZip}
,ORD_ADDR = #{ordAddr}
,ORD_ADDR_DTL = #{ordAddrDtl}
,STA_ORD_CD = #{staOrdCd}
,PROD_ID = #{prodId}
,PROD_BAR_NUM = #{prodBarNum}
,PROD_SIZE = #{prodSize}
,PROD_COLOR = #{prodColor}
,PROD_OPTION = #{prodOption}
,LGS_STO_ID = #{lgsStoId}
,ORD_LEND_STR_DTM = #{ordLendStrDtm}
,ORD_LEND_END_DTM = #{ordLendEndDtm}
WHERE PEN_ORD_ID = #{penOrdId}
AND PEN_STA_SEQ = #{penStaSeq}
AND STO_ID = #{stoId}

mapper.delete:
DELETE FROM PEN5000
