mapper.update:
UPDATE PRO1100 SET
MODIFY_DTM = CURRENT_TIMESTAMP
,MODIFY_USR_ID = #{usrId}
,MODIFY_USR_IP = #{accessIp}
,PROD_QTY = 1
,PROD_NM = #{prodNm}
,ITEM_ID = #{itemId}
,SUB_ITEM = #{subItem}
,PROD_SUP_YN = #{prodSupYn}
,PROD_SUP_PRICE = #{prodSupPrice}
,PROD_OFL_PRICE = #{prodOflPrice}
,RENTAL_PRICE = #{rentalPrice}
,RENTAL_PRICE_EXTN = #{rentalPriceExtn}
,SUP_ID = #{supId}
,USE_CD = #{useCd}
,PROD_STATE_CODE = #{prodStateCode}
,TAX_INFO_CD = #{taxInfoCd}
,PROD_PAY_CODE = #{prodPayCode}
,PROD_SYM = #{prodSym}
,PROD_WEIG = #{prodWeig}
,PROD_DETAIL = #{prodDetail}
,PROD_COLOR = #{prodColor}
,PROD_SIZE = #{prodSize}
,PROD_SIZE_DETAIL = #{prodSizeDetail}
,PROD_IMG_ATTR = #{prodImgAttr}
WHERE 1=1
AND PROD_ID = #{prodId}

UPDATE PRO1101 SET
