mapper.sql:
SELECT T1.* , (SELECT USR_NM FROM ENT1100 WHERE USR_ID = T1.USR_ID ) AS USR_NM , DATEDIFF(T1.PEN_APP_ED_DTM , DATE_FORMAT(NOW(),'%Y%m%d')) AS PEN_APP_REM_DAY FROM ( SELECT A.PEN_ID , A.PEN_NM , A.ENT_ID, A.ENT_USR_ID AS USR_ID, A.PEN_LTM_NUM , A.PEN_REC_GRA_CD, (SELECT SUB_CD_NM FROM CMM1100 WHERE MST_CD = 'PEN00001' AND SUB_CD = A.PEN_REC_GRA_CD) AS PEN_REC_GRA_NM , A.PEN_TYPE_CD , (SELECT SUB_CD_NM FROM CMM1100 WHERE MST_CD = 'PEN00002' AND SUB_CD = A.PEN_TYPE_CD ) AS PEN_TYPE_NM , A.PEN_CNM_TYPE_CD , (SELECT PEN_CNM_TYPE_CD FROM CMM1100 WHERE MST_CD = 'PEN00004' AND SUB_CD = A.PEN_CNM_TYPE_CD ) AS PEN_CNM_TYPE_NM , A.PEN_REC_TYPE_CD, A.PEN_REC_TYPE_TXT, A.PEN_REMARK, A.PEN_EXPI_ST_DTM , A.PEN_EXPI_ED_DTM , CONCAT(DATE_FORMAT(A.PEN_EXPI_ST_DTM, '%Y-%m-%d') , ' ~ ' , DATE_FORMAT(A.PEN_EXPI_ED_DTM, '%Y-%m-%d')) AS PEN_EXPI_DTM , DATEDIFF(DATE_FORMAT(NOW(),'%Y%m%d') ,A.PEN_EXPI_ST_DTM) AS PEN_EXPI_REM_DAY, A.PEN_GENDER , (SELECT SUB_CD_NM FROM CMM1100 WHERE MST_CD = 'CMM00002' AND SUB_CD = A.PEN_GENDER) AS PEN_GENDER_NM , A.PEN_BIRTH , TRUNCATE((TO_DAYS(NOW()) - TO_DAYS(DATE_FORMAT(A.PEN_BIRTH, '%Y-%m-%d'))) / 365, 0) AS PEN_AGE , CASE WHEN A.PEN_APP_ED_DTM3 IS NOT NULL THEN A.PEN_APP_ED_DTM3 WHEN A.PEN_APP_ED_DTM3 IS NULL AND A.PEN_APP_ED_DTM2 IS NOT NULL THEN PEN_APP_ED_DTM2 ELSE A.PEN_APP_ED_DTM1 END PEN_APP_ED_DTM , A.PEN_JUMIN, A.PEN_ZIP, A.PEN_ADDR , A.PEN_ADDR_DTL , A.PEN_CON_NUM SDB_PEN_CON_NUM, A.PEN_CON_PNUM SDB_PEN_CON_PNUM , A.PEN_PRO_TYPE_CD, (SELECT SUB_CD_NM FROM CMM1100 WHERE MST_CD = 'PEN00016' AND SUB_CD = A.PEN_PRO_TYPE_CD) AS PEN_PRO_TYPE_NM , A.PEN_PRO_NM, A.PEN_PRO_ZIP , A.PEN_PRO_ADDR , A.PEN_PRO_ADDR_DTL , A.PEN_PRO_CON_NUM SDB_PEN_PRO_CON_NUM, A.PEN_PRO_CON_PNUM SDB_PEN_PRO_CON_PNUM, A.PEN_PRO_BIRTH, A.PEN_PRO_REL, A.PEN_PRO_REL_ETC, A.PEN_PRO_EMAIL SDB_PEN_PRO_EMAIL, A.APP_CD , (SELECT SUB_CD_NM FROM CMM1100 WHERE MST_CD = 'PEN00007' AND SUB_CD = A.APP_CD) AS APP_CD_NM , A.CA_CEN_YN , A.REG_DTM , DATE_FORMAT(A.REG_DTM, '%Y.%m.%d') AS REG_DT FROM PEN1001 A WHERE 1=1 AND A.DEL_YN = 'N' ) T1 WHERE 1=1
AND T1.APP_CD = #{appCd}
AND ( T1.PEN_NM LIKE CONCAT('%', #{penNm}, '%') OR T1.PEN_PRO_NM LIKE CONCAT('%', #{penNm}, '%') )
AND T1.PEN_TYPE_CD = #{penTypeCd}
AND T1.PEN_REC_GRA_CD = #{penRecGraCd}
AND T1.PEN_ID = #{penId}
AND T1.USR_ID = #{usrId}
ORDER BY REG_DTM DESC

SELECT T1.* , (SELECT USR_NM FROM ENT1100 WHERE USR_ID = T1.USR_ID ) AS USR_NM , DATEDIFF(T1.PEN_APP_ED_DTM , DATE_FORMAT(NOW(),'%Y%m%d')) AS PEN_APP_REM_DAY FROM ( SELECT A.PEN_ID , C.ENT_ID , A.PEN_NM , A.PEN_LTM_NUM , A.PEN_REC_GRA_CD, (SELECT SUB_CD_NM FROM CMM1100 WHERE MST_CD = 'PEN00001' AND SUB_CD = A.PEN_REC_GRA_CD) AS PEN_REC_GRA_NM , A.PEN_TYPE_CD , (SELECT SUB_CD_NM FROM CMM1100 WHERE MST_CD = 'PEN00002' AND SUB_CD = A.PEN_TYPE_CD ) AS PEN_TYPE_NM , A.PEN_CNM_TYPE_CD , (SELECT PEN_CNM_TYPE_CD FROM CMM1100 WHERE MST_CD = 'PEN00004' AND SUB_CD = A.PEN_CNM_TYPE_CD ) AS PEN_CNM_TYPE_NM , A.PEN_REC_TYPE_CD, A.PEN_REC_TYPE_TXT, A.PEN_REMARK, A.PEN_EXPI_ST_DTM , A.PEN_EXPI_ED_DTM , CONCAT(DATE_FORMAT(A.PEN_EXPI_ST_DTM, '%Y-%m-%d') , ' ~ ' , DATE_FORMAT(A.PEN_EXPI_ED_DTM, '%Y-%m-%d')) AS PEN_EXPI_DTM , DATEDIFF(DATE_FORMAT(NOW(),'%Y%m%d') ,A.PEN_EXPI_ST_DTM) AS PEN_EXPI_REM_DAY, A.PEN_GENDER , (SELECT SUB_CD_NM FROM CMM1100 WHERE MST_CD = 'CMM00002' AND SUB_CD = A.PEN_GENDER) AS PEN_GENDER_NM , A.PEN_BIRTH , TRUNCATE((TO_DAYS(NOW()) - TO_DAYS(DATE_FORMAT(A.PEN_BIRTH, '%Y-%m-%d'))) / 365, 0) AS PEN_AGE , CASE WHEN A.PEN_APP_ED_DTM3 IS NOT NULL THEN A.PEN_APP_ED_DTM3 WHEN A.PEN_APP_ED_DTM3 IS NULL AND A.PEN_APP_ED_DTM2 IS NOT NULL THEN PEN_APP_ED_DTM2 ELSE A.PEN_APP_ED_DTM1 END PEN_APP_ED_DTM , A.PEN_JUMIN, A.PEN_ZIP, A.PEN_ADDR , A.PEN_ADDR_DTL , A.PEN_CON_NUM SDB_PEN_CON_NUM, A.PEN_CON_PNUM SDB_PEN_CON_PNUM , A.PEN_PRO_TYPE_CD, (SELECT SUB_CD_NM FROM CMM1100 WHERE MST_CD = 'PEN00016' AND SUB_CD = A.PEN_PRO_TYPE_CD) AS PEN_PRO_TYPE_NM , A.PEN_PRO_NM, A.PEN_PRO_ZIP , A.PEN_PRO_ADDR , A.PEN_PRO_ADDR_DTL , A.PEN_PRO_CON_NUM SDB_PEN_PRO_CON_NUM, A.PEN_PRO_CON_PNUM SDB_PEN_PRO_CON_PNUM, A.PEN_PRO_BIRTH, A.PEN_PRO_REL, A.PEN_PRO_REL_ETC, A.PEN_PRO_EMAIL SDB_PEN_PRO_EMAIL, C.ENT_USR_ID AS USR_ID, A.APP_CD , (SELECT SUB_CD_NM FROM CMM1100 WHERE MST_CD = 'PEN00007' AND SUB_CD = A.APP_CD) AS APP_CD_NM , A.CA_CEN_YN , A.REG_DTM , DATE_FORMAT(A.REG_DTM, '%Y.%m.%d') AS REG_DT , B.ORD_LEND_END_DTM , DATEDIFF(B.ORD_LEND_END_DTM, DATE_FORMAT(NOW(),'%Y%m%d')) AS ORD_LEND_REM_DAY, CASE WHEN REC_ID IS NULL THEN 'N' ELSE 'Y' END AS REC_YN FROM ( SELECT * FROM ENTPEN1000 WHERE ENT_ID = #{entId} AND DEL_YN = 'N') C LEFT JOIN PEN1000 A ON C.PEN_ID = A.PEN_ID LEFT JOIN pen1500 P15 ON P15.PEN_ID = A.PEN_ID LEFT OUTER JOIN ( SELECT B.PEN_ID, B.ORD_LEND_END_DTM FROM ( SELECT PEN_ID, ORD_LEND_END_DTM , ROW_NUMBER() OVER ( PARTITION BY PEN_ID ORDER BY ORD_LEND_END_DTM DESC ) RN FROM PEN5000 WHERE ORD_STATUS = '01' AND STA_ORD_CD = '03' ) B WHERE B.RN = '1' ) B ON B.PEN_ID = A.PEN_ID WHERE 1=1 AND A.DEL_YN = 'N' ) T1 WHERE 1=1
AND T1.APP_CD = #{appCd}
AND T1.PEN_NM LIKE CONCAT('%', #{penNm}, '%')
AND T1.PEN_PRO_NM LIKE CONCAT('%', #{penProNm}, '%')
AND T1.PEN_LTM_NUM LIKE CONCAT('%', #{penLtmNum}, '%')
AND T1.PEN_TYPE_CD = #{penTypeCd}
AND T1.PEN_REC_GRA_CD = #{penRecGraCd}
AND T1.PEN_ID = #{penId}
AND T1.USR_ID = #{usrId}
ORDER BY REG_DTM DESC

SELECT A.*, CASE WHEN A.HELPER_TYPE IS NULL THEN HELPER_TYPE_ETC ELSE (SELECT SUB_CD_NM FROM cmm1100 WHERE MST_CD = 'REC00002' AND SUB_CD = A.HELPER_TYPE) END AS HELPER_TYPE_NM, (SELECT SUB_CD_NM FROM cmm1100 WHERE MST_CD = 'REC00001' AND SUB_CD = A.PSCL_STATE1) AS PSCL_STATE1_NM, (SELECT SUB_CD_NM FROM cmm1100 WHERE MST_CD = 'REC00001' AND SUB_CD = A.PSCL_STATE2) AS PSCL_STATE2_NM, (SELECT SUB_CD_NM FROM cmm1100 WHERE MST_CD = 'REC00001' AND SUB_CD = A.PSCL_STATE3) AS PSCL_STATE3_NM, (SELECT SUB_CD_NM FROM cmm1100 WHERE MST_CD = 'REC00001' AND SUB_CD = A.PSCL_STATE4) AS PSCL_STATE4_NM, (SELECT SUB_CD_NM FROM cmm1100 WHERE MST_CD = 'REC00001' AND SUB_CD = A.PSCL_STATE5) AS PSCL_STATE5_NM, (SELECT SUB_CD_NM FROM cmm1100 WHERE MST_CD = 'REC00001' AND SUB_CD = A.PSCL_STATE6) AS PSCL_STATE6_NM, (SELECT SUB_CD_NM FROM cmm1100 WHERE MST_CD = 'REC00003' AND SUB_CD = A.HOME_ENV) AS HOME_ENV_NM, (SELECT SUB_CD_NM FROM cmm1100 WHERE MST_CD = 'REC00004' AND SUB_CD = A.HOME_TYPE) AS HOME_TYPE_NM, (SELECT SUB_CD_NM FROM cmm1100 WHERE MST_CD = 'REC00005' AND SUB_CD = A.INMATE) AS INMATE_NM, (SELECT ENT_NM FROM ENT1000 WHERE ENT_ID = A.ENT_ID) AS ENT_NM, (SELECT USR_NM FROM ENT1100 WHERE USR_ID = A.USR_ID) AS USR_NM, (SELECT PEN_NM FROM PEN1000 WHERE PEN_ID = A.PEN_ID) AS PEN_NM FROM pen1500 A JOIN pen1000 p on p.PEN_ID = A.PEN_ID AND P.DEL_YN = 'N' WHERE 1=1
AND A.ENT_ID = #{entId}
AND P.PEN_NM LIKE CONCAT('%', #{penNm}, '%')
AND P.PEN_PRO_NM LIKE CONCAT('%', #{penProNm}, '%')
AND P.PEN_LTM_NUM LIKE CONCAT('%', #{penLtmNum}, '%')
AND P.PEN_REC_GRA_CD = #{penRecGraCd}
AND A.PEN_ID = #{penId}
AND A.REC_ID = #{recId}
ORDER BY A.REG_DTM DESC

