mapper.select:
SELECT PPC_ID
FROM PRO1102
WHERE DEL_YN = 'N'
AND PROD_ID = #{prodId}
AND ENT_ID = #{entId}
LIMIT 1

SELECT
    PROD_BAR_NUM
FROM PRO1103 A
JOIN PRO1102 B ON B.PPC_ID = A.PPC_ID
JOIN PRO1100 C ON C.PROD_ID = B.PROD_ID
WHERE A.DEL_YN = 'N' AND A.STATE_CD != '02'
    AND PROD_BAR_NUM = #{prodBarNum}
    AND B.PROD_ID = #{prodId}
    AND B.ENT_ID = #{entId}

SELECT
    D.PROD_BAR_NUM
FROM PRO1100 A
    JOIN PRO1000 B ON A.ITEM_ID = B.ITEM_ID
    JOIN PRO1102 C ON C.PROD_ID = A.PROD_ID AND C.DEL_YN = 'N'
    JOIN PRO1103 D ON D.PPC_ID = C.PPC_ID AND D.DEL_YN = 'N'
WHERE D.DEL_YN = 'N' AND D.STATE_CD = '01'
AND D.STO_ID = #{stoId}
AND A.PROD_ID = #{prodId}
AND D.PROD_COLOR = #{prodColor}
AND D.PROD_SIZE = #{prodSize}
AND D.PROD_OPTION = #{prodOption}
AND D.REG_USR_ID = #{usrId}
AND C.ENT_ID = #{entId}
ORDER BY D.REG_DTM, PROD_BAR_NUM

SELECT
    C.PPC_ID,
    A.PROD_ID,
    A.PROD_NM,
    A.GUBUN,
    B.ITEM_ID,
    B.ITEM_NM,
    A.PROD_SUP_YN,
    A.PROD_PAY_CODE,
    D.PROD_COLOR,
    D.PROD_SIZE,
    D.PROD_OPTION,
    D.INITIAL_CONTRACT_DATE,
    COUNT(*) QUANTITY
FROM PRO1100 A
    JOIN PRO1000 B ON A.ITEM_ID = B.ITEM_ID
    JOIN PRO1102 C ON C.PROD_ID = A.PROD_ID AND C.DEL_YN = 'N'
    JOIN PRO1103 D ON D.PPC_ID = C.PPC_ID AND D.DEL_YN = 'N'
WHERE D.DEL_YN = 'N'
AND D.STATE_CD IN ('01',#{item})
AND D.PROD_BAR_NUM = #{prodBarNum}
AND D.PROD_COLOR = #{prodColor}
AND D.PROD_SIZE = #{prodSize}
AND D.PROD_OPTION = #{prodOption}
AND C.ENT_ID = #{entId}
AND D.REG_USR_ID = #{usrId}
AND D.STO_ID = #{stoId}
AND A.PROD_ID = #{prodId}
AND A.PROD_PAY_CODE = #{prodPayCode}
AND A.GUBUN = #{gubun}
GROUP BY PROD_NM,PROD_COLOR,PROD_SIZE,PROD_OPTION
ORDER BY D.REG_DTM, PROD_BAR_NUM

SELECT COUNT(*) FROM PRO1100 A JOIN PRO1000 B ON A.ITEM_ID = B.ITEM_ID JOIN PRO1102 C ON C.PROD_ID = A.PROD_ID AND C.DEL_YN = 'N' JOIN PRO1103 D ON D.PPC_ID = C.PPC_ID AND D.DEL_YN = 'N' LEFT JOIN ( SELECT PEN_ORD_ID, PEN.UUID, STO_ID, PEN_ID, ORD_LEND_STR_DTM, ORD_LEND_END_DTM, REG_DTM FROM pen5000 PEN LEFT JOIN (SELECT DISTINCT UUID, EFORM_FILE_PATH, DOCUMENT_ID FROM eform_filelist_table WHERE DOCUMENT_ID = 'ALL') EFORM ON EFORM.UUID = PEN.UUID WHERE STA_ORD_CD != '04' ) ORD ON ORD.STO_ID = D.STO_ID AND ORD.REG_DTM = (SELECT MAX(REG_DTM) FROM pen5000 T1 WHERE T1.STO_ID = D.STO_ID) WHERE D.DEL_YN = 'N' AND D.STATE_CD IN #{item}
AND (D.PROD_SIZE like concat('%',#{searchOption},'%') OR D.PROD_COLOR like concat('%',#{searchOption},'%') OR REGEXP_REPLACE(D.PROD_OPTION, '\[gs\](.*?)\[gs\]' ,'') like concat('%',#{searchOption},'%'))
AND D.PROD_BAR_NUM = #{prodBarNum} AND C.ENT_ID = #{entId} AND D.STO_ID = #{stoId} AND A.PROD_ID = #{prodId} ORDER BY D.REG_DTM, PROD_BAR_NUM ASC

SELECT D.STO_ID, A.PROD_ID, A.PROD_NM, A.GUBUN, (SELECT SUB_CD_NM FROM cmm1100 WHERE MST_CD = 'PRO00001' AND SUB_CD = A.GUBUN) AS GUBUN_NM, B.ITEM_ID, B.ITEM_NM, A.PROD_SUP_YN, CASE A.PROD_SUP_YN WHEN 'Y' THEN '유통' WHEN 'N' THEN '미유통' END AS PROD_SUP_NM, A.PROD_PAY_CODE, D.PROD_COLOR, D.PROD_SIZE, D.PROD_OPTION, D.STATE_CD, (SELECT SUB_CD_NM FROM cmm1100 WHERE MST_CD = 'PRO00003' AND SUB_CD = D.STATE_CD) AS STATE_NM, CASE WHEN STATE_CD != '01' THEN EFORM_URL END AS EFORM_URL, CASE WHEN STATE_CD != '01' THEN PEN_ORD_ID END AS PEN_ORD_ID, CASE WHEN STATE_CD != '01' THEN ORD_LEND_STR_DTM END AS ORD_LEND_STR_DTM, CASE WHEN STATE_CD != '01' THEN ORD_LEND_END_DTM END AS ORD_LEND_END_DTM, ORD.PEN_ID PEN_ID, (SELECT PEN_NM FROM pen1000 WHERE PEN_ID = ORD.PEN_ID) AS PEN_NM, PROD_BAR_NUM, D.MODIFY_DTM, D.REG_DTM, D.INITIAL_CONTRACT_DATE FROM PRO1100 A JOIN PRO1000 B ON A.ITEM_ID = B.ITEM_ID JOIN PRO1102 C ON C.PROD_ID = A.PROD_ID AND C.DEL_YN = 'N' JOIN PRO1103 D ON D.PPC_ID = C.PPC_ID AND D.DEL_YN = 'N' LEFT JOIN ( SELECT PEN_ORD_ID, PEN.UUID, STO_ID, PEN_ID, ORD_LEND_STR_DTM, ORD_LEND_END_DTM, REG_DTM, CONCAT(#{downloadUrl},'uuid=',PEN.UUID,'&path=',EFORM_FILE_PATH,'&gubn=',DOCUMENT_ID) AS EFORM_URL FROM pen5000 PEN LEFT JOIN (SELECT DISTINCT UUID, EFORM_FILE_PATH, DOCUMENT_ID FROM eform_filelist_table WHERE DOCUMENT_ID = 'ALL') EFORM ON EFORM.UUID = PEN.UUID WHERE STA_ORD_CD != '04' ) ORD ON ORD.STO_ID = D.STO_ID AND ORD.REG_DTM = (SELECT MAX(REG_DTM) FROM pen5000 T1 WHERE T1.STO_ID = D.STO_ID) WHERE D.DEL_YN = 'N' AND D.STATE_CD IN #{item}
AND (D.PROD_SIZE like concat('%',#{searchOption},'%') OR D.PROD_COLOR like concat('%',#{searchOption},'%') OR REGEXP_REPLACE(D.PROD_OPTION, '\[gs\](.*?)\[gs\]' ,'') like concat('%',#{searchOption},'%'))
AND D.PROD_BAR_NUM = #{prodBarNum} AND C.ENT_ID = #{entId} AND D.STO_ID = #{stoId} AND A.PROD_ID = #{prodId} ORDER BY D.REG_DTM, PROD_BAR_NUM ASC
LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM(
    SELECT
        COUNT(*)
    FROM PRO1100 A
    JOIN PRO1000 B ON A.ITEM_ID = B.ITEM_ID
    JOIN PRO1102 C ON C.PROD_ID = A.PROD_ID AND C.DEL_YN = 'N'
    JOIN PRO1103 D ON D.PPC_ID = C.PPC_ID AND D.DEL_YN = 'N'
    WHERE D.DEL_YN = 'N'
    AND C.ENT_ID = #{entId}
    AND A.PROD_NM like concat('%',#{prodNm},'%')
    AND A.PROD_ID = #{prodId}
    AND A.PROD_PAY_CODE = #{prodPayCode}
    AND A.GUBUN = #{gubun}
    AND A.TAX_INFO_CD = #{taxInfoCd}
    GROUP BY A.PROD_ID
) A

