INSERT INTO PRO1103
(
STO_ID
,PPC_ID,			STATE_CD, 			PROD_BAR_NUM
,PROD_COLOR,        PROD_SIZE,          PROD_OPTION
,PROD_MANU_DATE,	STO_MEMO,	        DEL_YN
,REG_DTM,			REG_USR_ID, 		REG_USR_IP
,MODIFY_DTM,		MODIFY_USR_ID,		MODIFY_USR_IP
)
VALUES

