,CURRENT_TIMESTAMP, #{item.usrId}, #{item.accessIp}
)

mapper.update:
UPDATE PRO1103 SET
MODIFY_DTM		= CURRENT_TIMESTAMP
,MODIFY_USR_ID	= #{usrId}
,MODIFY_USR_IP = #{accessIp}
,PPC_ID= #{ppcId}
,PROD_BAR_NUM= #{prodBarNum}
,STATE_CD = #{stateCd}
,PROD_COLOR = #{prodColor}
,PROD_SIZE = #{prodSize}
,PROD_OPTION = #{prodOption}
,PROD_MANU_DATE = #{prodManuDate}
,STO_MEMO = #{stoMemo}
,DEL_YN = #{delYn}
,INITIAL_CONTRACT_DATE = #{initialContractDate}
WHERE STO_ID = #{stoId}
